import styled from "styled-components";
import React from "react";

const Header = (props) => {
  return (
    <Nav>
      <Logo>
        <img src="/images/logo.svg" alt="Disney+" />
      </Logo>
      <NavMenu>
        <a href="/home">
          <img src="/images/logo-icon.svg" alt="HOME" />
        </a>
        <span>HOME</span>
      </NavMenu>
    </Nav>
  );
};

const Nav = styled.nav`
  font-size: 18px;
  position: fixed;
  height: 70px;
  top: 0;
  right: 0;
  left: 0;
  background-color: #090b13;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 36px;
  z-index: 3;
`;

const Logo = styled.a`
  width: 80px;
  padding: 0;
  max-height: 70px;
  display: inline-block;
  margin-bottom: 4px;
  img {
    display: block;
    width: 100%;
  }
`;

const NavMenu = styled.div`
  align-items: center;
  display: flex;
  flex-flow: row nowrap;
  height: 100%;
  justify-content: flex-end;
  margin: 0;
  padding: 0;
  position: relative;
  margin-right: auto;
  margin-left: 25px;

  //   @media (max-width: 768px) {
  //     display: none;
  //   }
`;

export default Header;
